
class Solution:
    def longestValidParentheses(self, s: str) -> int:
        longest = 0
        last_start = -1

        stack = []

        for i in range(0, len(s)):

            if s[i] == '(':
                stack.append(i)
            elif s[i] == ')':

                if not stack:
                    last_start = i
                    continue

                stack.pop()

                if stack:
                    longest = max(longest, i - stack[-1])
                else:
                    longest = max(longest, i - last_start)


        return longest




